USE individual_project;

DROP TABLE IF EXISTS `player`;

CREATE TABLE `individual_project`.`player`
(
   `id` integer not null auto_increment,
   `flag` varchar(255) null,
   `country` varchar(255) null,
   `name` varchar(255) null,
   `xAxis` varchar(255) null,
   `team` varchar(255) null,
   `cm360` varchar(255) null,
   `hz` varchar(255) null,
   `windowsSensitivity` varchar(255) null,
   `sensitivity` varchar(255) null,
   `zoomSensitivityRatio` varchar(255) null,
   `mPitch` varchar(255) null,
   `mYaw` varchar(255) null,
   `mouseAcceleration` varchar(255) null,
   `mRawinput` varchar(255) null,
   `rinputExe` varchar(255) null,
   `resolution` varchar(255) null,
   `aspectRatio` varchar(255) null,
   `finalAspectRatio` varchar(255) null,
   `aspectRatioDescription` varchar(255) null,
   `monitorHz` varchar(255) null,
   `lightboostBlurReduction` varchar(255) null,
   `digitalVibranceSaturation` varchar(255) null,
   `monitor` varchar(255) null,
   `mouse` varchar(255) null,
   `mousepad` varchar(255) null,
   `Inactivity` varchar(255) null,
   `notes` varchar(255) null,
   `lastUpdate` varchar(255) null,
   `picture` varchar(255) null,
   PRIMARY KEY (`id`)
)ENGINE=InnoDB DEFAULT CHARSET=latin1;


USE individual_project;

DROP TABLE IF EXISTS `users`;

CREATE TABLE `individual_project`.`users`
(
   `id` integer not null auto_increment,
   `credentials` varchar(255) null,
   `email` varchar(255) null,
   `password` varchar(255) null,
   PRIMARY KEY (`id`)
)ENGINE=InnoDB DEFAULT CHARSET=latin1;

